def transform(sequence):
    trans_seq = ''
    for ch in sequence:
        if ch == '0':
            trans_seq += '0'
        elif ch == '3':
            trans_seq += '4'
        elif ch == '33':
            trans_seq += '88'
        elif ch == '333':
            trans_seq += '444'
        elif ch == '30':
            trans_seq += '60'
        elif ch == '300':
            trans_seq += '600'
    return trans_seq.replace('4','8').replace('6','4').replace('8','6')

