def transform(sequence):
    trans_dict = {'0': '0', '3': '4', '30': '60', '33': '88'}
    result_sequence = ''.join(trans_dict.get(sequence[i:i+2], sequence[i]) for i in range(0, len(sequence), 2))
    return result_sequence
