def transform(sequence):
    mapping = {'0': '0', '7': '2', '1': '5'}
    return ''.join([mapping.get(ch, ch) for ch in sequence])

def transform(sequence):
    mapping = {'0': '0', '7': '2', '77': '22', '77': '55', '77': '55'}
    new_sequence = ''.join(mapping.get(sequence[i:i+2], sequence[i]) for i in range(0, len(sequence), 2))
    return new_sequence.replace('7', '5')
