def transform(sequence):
    sequence = list(map(int, sequence))
    result = []
    for i in sequence:
        if i == 0:
            result.append(0)
        elif i == 4:
            result.append(2)
        elif i == 1:
            result.append(9)
        else:
            result.append(i * 2)
