def transform(sequence):
    n = len(sequence)
    idx = sequence.index('9')
    return '0' * idx + '9' + sequence[1:idx][::-1] + '0' * (n - idx - len(sequence[1:idx]) - 1)

def transform(sequence):
    seq = list(sequence)
    nine_index = seq.index('9')
    new_seq = seq[:nine_index][::-1] + seq[nine_index:]

    return ''.join(new_seq)

def transform(sequence):
    nines = sequence.index("9")
    return sequence[:nines].rjust(len(sequence) - nines, '0') + sequence[nines:].ljust(nines, '0')

def transform(sequence):
    idx = sequence.find('090')
    return sequence[idx:] + sequence[:idx]

def transform(sequence):
    parts = sequence.split('0')
    parts.reverse()
    return '0'.join(parts)
