def transform(sequence):
    splitted = sequence.split('0')
    transformed = ['0' * val.count('0') + val[-1] * 4 if val else '' for val in splitted]
    return '0'.join(transformed)

def transform(sequence):
    new_sequence = ''
    for i in range(len(sequence)):
        if sequence[i-1] != '0' and sequence[i] == '0':
            new_sequence += sequence[i-1]
        else:
            new_sequence += sequence[i]
    return new_sequence
