def transform(sequence):
    if '7' in sequence:
        index = sequence.lastIndexOf('7')
        sequence = sequence[:index+1] + '7'*(9 - (index+1)) + sequence[index+10:]
    elif '4' in sequence:
        index = sequence.lastIndexOf('4')
        sequence = sequence[:index+1] + '4'*(9 - (index+1)) + sequence[index+10:]
    return sequence

