def transform(sequence):
    string_seq = str(sequence)
    first_non_zero_idx = string_seq.find('0')
    nonzero_number = string_seq[first_non_zero_idx - 1]
    converted_seq = nonzero_number * first_non_zero_idx + string_seq[first_non_zero_idx:]
    return int(converted_seq)
