    transformed = []
    for i in range(len(sequence)):
        if sequence[i] == '7':
            transformed.append('7')
            if sequence[i-1] != '7':
                for j in range(i-1,-1,-1):
                    if sequence[j] == '0':
                        transformed[j] = '7'
                    else:
