def transform(sequence):
    transformation = {'3': '9', '0': '0', '30': '40', '33': '99', '03': '02', '00': '00'}

    transformed_sequence = ''
    i = 0
    while i < len(sequence):
        if i < len(sequence) - 1 and sequence[i:i+2] in transformation:
            transformed_sequence += transformation[sequence[i:i+2]]
