def transform(sequence):
    transformed = ''
    for s in sequence:
        if s == '0':
            transformed += '0'
        elif s == '1':
            transformed += '4'
        elif s == '2':
            transformed += '8'
        elif s == '3':
            transformed += '9'
    return transformed

def transform(sequence):
    mapping = {'0': '0', '1': '4', '10': '9', '11': '8'}
    result = ''
    i = 0
    while i < len(sequence):
        if i+1 < len(sequence) and sequence[i:i+2] in mapping:
            result += mapping[sequence[i:i+2]]
            i += 2
        else:
            result += mapping[sequence[i]]
            i += 1
    return result
