def transform(sequence):
    first_zero_idx = sequence.index('0')
    non_zero_idx = next(i for i,c in enumerate(sequence[first_zero_idx:], first_zero_idx) if c != '0')
    last_zero_idx = non_zero_idx - 1
    last_non_zero_idx = next(i for i,c in enumerate(sequence[non_zero_idx:], non_zero_idx) if c == '0') - 1

    return sequence[:first_zero_idx] + '0'*(last_zero_idx - first_zero_idx + 1) + sequence[non_zero_idx:last_non_zero_idx] + '0'*(len(sequence) - last_non_zero_idx)

def transform(sequence):
    first, *middle, last = sequence
    return "".join([first] + sorted(middle) + [last])
