def transform(sequence):
    zero_count = sequence.count('0')
    split_sequence = sequence.split('0')
    non_zero_part = [x for x in split_sequence if x != '']
    return '0' * zero_count + ''.join(non_zero_part)

def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)):
        if sequence[i] == '9':
            sequence[i], sequence[i-1] = sequence[i-1], sequence[i]
    return ''.join(sequence)

def transform(sequence):
    index = sequence.index('9')
    sequence = list(sequence)
    sequence[index-1], sequence[index] = sequence[index], sequence[index-1]
    return "".join(sequence)

def transform(sequence):
    sequence_string = list(sequence)
    for i in range(len(sequence_string)-1):
        if sequence_string[i] == '0' and sequence_string[i+1] != '0':
            sequence_string[i], sequence_string[i+1] = sequence_string[i+1], sequence_string[i]
    return ''.join(sequence_string)
