def transform(sequence):
    lst = list(sequence)
    idx = [i for i, e in enumerate(lst) if e != "0"]
    a, b = idx[0], idx[-1]
    lst[a], lst[b] = lst[b], lst[a]
    return "".join(lst)

def transform(sequence):
    first_non_zero_index = next((i for i, x in enumerate(sequence) if x != '0'), None)
    number_of_zeros = sequence.count('0')
    return '0'*number_of_zeros + sequence[first_non_zero_index:].replace('0', '') + '0'*(len(sequence) - number_of_zeros - len(sequence[first_non_zero_index:].replace('0', '')))

def transform(sequence):
    index = sequence.index('0', 1)
    return sequence[index:] + sequence[1:index]
