def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)):
        if sequence[i] == '0' and sequence[i + 1] != '0':
            sequence[i] = sequence[i + 1]
            break
    for i in range(len(sequence) - 1, -1, -1):
        if sequence[i] == '0' and sequence[i - 1] != '0':
            sequence[i] = sequence[i - 1]
            break
    return ''.join(sequence)

