def transform(sequence):
    count = sequence.count('0')
    zeros = ['0' for i in range(count)]
    rest = [sequence[i] for i in range(len(sequence)) if sequence[i] != '0']
    transform_sequence = zeros + rest + zeros
    return ''.join(transform_sequence)

def transform(sequence):
    first = sequence.find('1')
    last = sequence.rfind('1')
    if first == -1:
        first = sequence.find('5')
        last = sequence.rfind('5')
        if first == -1:
            first = sequence.find('6')
            last = sequence.rfind('6')
    return sequence[:first-1] + '0' + sequence[first-1:last+1] + '0' + sequence[last+2:]

def transform(sequence):
    split_sequence = sequence.split('0')
    transformed_sequence = [seq + '0' for seq in split_sequence if seq != '']
    return '0'.join(transformed_sequence)
