def transform(sequence):
    first_non_zero = next((i for i, x in enumerate(sequence) if x != '0'), None)
    last_non_zero = len(sequence) - 1 - next((i for i, x in enumerate(sequence[::-1]) if x != '0'), None)

    return '0'*(first_non_zero-1) + sequence[first_non_zero:last_non_zero+1] + '0'*(len(sequence)-last_non_zero)

