def transform(sequence):
    length = len(sequence)
    for i in range(length - 2):
        if sequence[i] != '0' and sequence[i + 1] == '0':
            sequence = sequence[:i + 1] + sequence[i] + sequence[i + 2:]
        if sequence[i + 2] != '0' and sequence[i + 1] == '0':
            sequence = sequence[:i + 1] + sequence[i + 2] + sequence[i + 2:]
    return sequence

def transform(sequence):
    num = sequence.strip('0')
    return sequence.replace(num, '0'+num[:-1]+'0'+num[-1])
