def transform(sequence):
    split_sequence = list(sequence)

    for i in range(len(split_sequence) - 1):
        if split_sequence[i] != '0' and split_sequence[i + 1] == '0':
            split_sequence[i + 1] = split_sequence[i]

    return "".join(split_sequence)

def transform(sequence):
    number = sequence.count('0')
    return sequence.replace('00', str(number), 1) if number < 10 else sequence.replace('00', str(10), 1)

def transform(sequence):
    prefix, mid, suffix = sequence.rpartition('0')
    return prefix + mid.replace('0', prefix[-1]) + suffix
