            sequence[i] = trans_dict[sequence[i]]
        elif sequence[i] == '0':
            sequence[i] = trans_dict[sequence[i]]
    sequence = "".join(sequence)
    sequence = sequence.replace("88", "66").replace("80", "60").replace("800", "600")
    return sequence

