def transform(sequence):
    transformed_sequence = ""
    sequence_length = len(sequence)

    for i in range(sequence_length - 2):
        if sequence[i] == sequence[i + 1] == sequence[i + 2] != '0':
            transformed_sequence += sequence[i] + '0' + '0'
        else:
            transformed_sequence += sequence[i]

    transformed_sequence += sequence[-2:]
    return transformed_sequence

def transform(sequence):
    sequence = list(sequence)
    first_occurence = True
    for i in range(len(sequence)):
        if sequence[i] != '0':
            if first_occurence:
                sequence[i] = str(int(sequence[i]) - 1)
                first_occurence = False
            else:
                sequence[i] = '0'
                first_occurence = True
    return ''.join(sequence)
