def transform(sequence):
    remains = sequence
    new_sequence = ""
    while True:
        cluster = remains[:3]
        if cluster == "":
            break
        if cluster[0] == '0':
            new_sequence += cluster
            remains = remains[3:]
        else:
            new_sequence += cluster[0] + cluster
            remains = remains[3:]
