def transform(sequence):
    new_sequence = ''
    number_tracker = ''
    count = 0
    for num in sequence:
        if num != '0':
            number_tracker = num
            new_sequence += num
            count += 1
        else:
            if count % 3 == 0 and number_tracker != '':
                new_sequence += number_tracker
            else:
                new_sequence += num
        count += 1
    return new_sequence

def transform(sequence):
    parts = sequence.split('000')
    for i in range(len(parts)-1):
        parts[i] = parts[i] + '0' * len(parts[i])
    return '000'.join(parts)

def transform(sequence):

    sequence_list = [sequence[i:i+4] for i in range(0, len(sequence), 4)]
    transformed_sequence = []
    counter = 0

    for s in sequence_list:

        if s == '0000':
            transformed_sequence.append(s)
            counter = 0
        else:
            counter += 1
            transformed_sequence.append(s[0]*4 + s[1]*(4-counter) + s[2]*(4-counter) + s[3]*(4-counter))

    return ''.join(transformed_sequence)
