def transform(sequence):
    transformed_sequence = ''
    for char in sequence:
        if char not in ['0', '2', '8', '3', '5', '7', '4']:
            return "Invalid sequence"
        else:
            if char == '8' or char == '7' or char == '4':
                transformed_sequence += '2' if char == '8' else ('3' if char == '7' else '5')
            else:
                transformed_sequence += char
    return transformed_sequence

def transform(sequence):
    transformed_sequence = ""
    for ch in sequence:
        if ch == '8' or ch == '7' or ch == '5' or ch == '4' or ch == '3' or ch == '2':
            transformed_sequence += sequence[sequence.index(ch)]
        else:
            transformed_sequence += ch
    return transformed_sequence
