def transform(sequence):
    sequence_as_list = list(sequence)
    for index in range(len(sequence_as_list)):
        if sequence_as_list[index] not in '07':
            sequence_as_list[index] = '2'
            if sequence_as_list[index+1] == '6':
                sequence_as_list[index+1] = '2'
            elif sequence_as_list[index+1] == '9':
                sequence_as_list[index+1] = '7'
            elif sequence_as_list[index+1] == '8':
                sequence_as_list[index+1] = '7'
    return "".join(sequence_as_list)
