def transform(sequence):
    result = ''
    for i in range(len(sequence)):
        if i >= 2:
            if sequence[i] != '0' and sequence[i-1] == sequence[i-2] == '0':
                result += str(int(sequence[i]) + int(sequence[i-1]) + int(sequence[i-2]))
            elif sequence[i] != '0':
                result += sequence[i]
            else:
                result += '0'
        else:
            result += sequence[i]
    return result
