def transform(sequence):
    transformed_sequence = ""
    count = 0
    for i in sequence:
        if i == '0':
            transformed_sequence += '0'
            count = 0
        else:
            transform_value = int(i) + count
            transformed_sequence += str(transform_value)
            count += 1
    return transformed_sequence

