def transform(sequence):
    result = ''
    for i in range(len(sequence)):
        if sequence[i] != '0':
            result += sequence[i]
        else:
            j = i
            while j < len(sequence) and sequence[j] == '0':
                j += 1
            if j < len(sequence):
                result += sequence[j]
            else:
                result += '0'
    return result

def transform(sequence):
    seq = list(map(int, sequence))
    for i in range(len(seq)-1, -1, -1):
        if seq[i] != 0:
            for j in range(i, -1, -1):
                if seq[j] == 0:
                    seq[j] = seq[i]
                else:
                    break
    return "".join(map(str, seq))
