def transform(sequence):
    transformed = ''
    for i in range(len(sequence)):
        if sequence[i:i+4] == '0000':
            transformed += sequence[i] * 4
        else:
            transformed += sequence[i]
    return transformed

def transform(sequence):
    transformation = {"0": "000", "1": "111", "2": "222", "3": "333", "4": "444", "5": "555", "6": "666", "7": "777", "8": "888", "9": "999"}
    transformed_sequence = "".join([transformation[char] if char in transformation else char for char in sequence])
    return transformed_sequence.replace("000", "0")

def transform(sequence):
    transformed = ''
    for i in sequence:
        if i == '0':
            transformed += '0'
        elif i == '1':
            transformed += '7'
        elif i == '2':
            transformed += '2'
        elif i == '6':
            transformed += '6'
        elif i == '7':
            transformed += '7'
        elif i == '8':
            transformed += '8'
