def transform(sequence):
    transform_dict = {
        '0': '0',
        '1': '1',
        '6': '6',
        '7': '7',
        '3': '3',
        '8': '8'
    }

    sequence = [transform_dict[i] if i in transform_dict else '0' for i in sequence]

    for i in range(len(sequence)):
        if sequence[i] in ['6', '7', '3', '8'] and sequence[i-1] in ['0', '1']:
            sequence[i] = sequence[i] * 3

    return ''.join(sequence)

def transform(sequence):
    seq_list = list(sequence)
    for i in range(len(seq_list)):
        if seq_list[i] == '0':
            continue
        else:
            num = int(seq_list[i])
            seq_list[i] = str(num) * num
    return ''.join(seq_list)

def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)-1):
        if sequence[i] != '0' and sequence[i+1] == '0':
            sequence[i+1] = sequence[i]
    return "".join(sequence)
