def transform(sequence):
    transformed = ''
    for s in sequence:
        if s == '0':
            transformed += '0'
        elif s == '3':
            transformed += '4'
        elif s == '6':
            transformed += '6'
        elif s == '4':
            transformed += '4'
        elif s == '5':
            transformed += '5'
        elif s == '8':
            transformed += '8'
        else:
            num = int(s)
            transformed += str(num * 3)
    return transformed

