def transform(sequence):
    transformed = ''
    for i in sequence:
        if i == '0':
            transformed += '0'
        elif i == '7':
            transformed += '7'
        elif i == '4':
            transformed += '4'
        elif i == '9':
            transformed += '9'
        elif i == '5':
            transformed += '5'
        else:
            transformed += str(int(i) + 1)
    return transformed
