def transform(sequence):
    seq_list = list(sequence)
    nonzero_indices = [i for i in range(len(seq_list)) if seq_list[i] != '0']

    for i in nonzero_indices[::-1]:
        while i < len(seq_list) - 1 and seq_list[i+1] == '0':
            seq_list[i], seq_list[i+1] = seq_list[i+1], seq_list[i]
            i += 1

    return ''.join(seq_list)

def transform(sequence):
    sequence = list(sequence)
    index_of_9 = sequence.index('9')
    sequence[index_of_9+1:], sequence[:index_of_9+1] = sequence[:index_of_9+1], sequence[index_of_9+1:]
    return ''.join(sequence)

def transform(sequence):
    seg1, seg2, seg3 = sequence.rsplit('00', 2)
    return '00'.join([seg1, seg3, seg2])

def transform(sequence):
    sequence = list(sequence)
    non_zero_indices = [i for i, x in enumerate(sequence) if x != '0']
    first_non_zero_index, last_non_zero_index = min(non_zero_indices), max(non_zero_indices)
    sequence[first_non_zero_index:last_non_zero_index + 1] = sorted(sequence[first_non_zero_index:last_non_zero_index + 1], reverse=True)
    return ''.join(sequence)
