def transform(sequence):
    transform_map = {'0':'0', '4':'6', '44':'77'}
    result = ''
    i = 0
    while i < len(sequence):
        if i+2<=len(sequence) and sequence[i:i+2] in transform_map:
            result += transform_map[sequence[i:i+2]]
            i += 2
        elif sequence[i] in transform_map:
            result += transform_map[sequence[i]]
            i += 1
    return result

def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)):
        if sequence[i] == '4':
            sequence[i] = '6'
        elif sequence[i] == '0':
            sequence[i] = sequence[i]
        else:
            sequence[i] = str(int(sequence[i]) + 1)
    return ''.join(sequence)
