def transform(sequence):
    first_index = sequence.find('0', sequence.find('0') + 1)
    sequence = list(sequence)
    sequence[first_index], sequence[-1] = sequence[-1], sequence[first_index]
    return ''.join(sequence)

def transform(sequence):
    transformed = []
    zero_found = False
    for s in sequence:
        if s == '0':
            if zero_found:
                transformed.append(s)
            else:
                transformed.insert(0, s)
        else:
            zero_found = True
            transformed.append(s)
    return ''.join(transformed)
