def transform(sequence):
    for i in range(len(sequence)-1, -1, -1):
        if sequence[i] == '0':
            continue
        else:
            sequence = sequence[:i] + '2' + sequence[i+1:]
            break
    for i in range(len(sequence)):
        if sequence[i] == '0':
            continue
        else:
            sequence = sequence[:i-1] + '0' + sequence[i:]
            break
    return sequence
