def transform(sequence):
    index4 = sequence.find('4')
    index7 = sequence.find('7')
    index5 = sequence.find('5')
    index6 = sequence.rfind('6')
    if index4 != -1:
        sequence = sequence[:index4-1] + '0' + sequence[index4:]
    if index7 != -1:
        sequence = sequence[:index7-1] + '0' + sequence[index7:]
    if index5 != -1:
        sequence = sequence[:index5-1] + '0' + sequence[index5:]
    if index6 != -1:
        sequence = sequence[:index6] + sequence[index6-1:-1] + '0'
    return sequence
