def transform(sequence):
    transformed_sequence = ""
    for s in sequence:
        if s != '0':
            if sequence.index(s) != 0 and sequence[sequence.index(s) - 1] == '0':
                transformed_sequence += s
            elif sequence.index(s) != len(sequence) - 1 and sequence[sequence.index(s) + 1] == '0':
                transformed_sequence += s
            else:
                transformed_sequence += '0'
        else:
            transformed_sequence += '0'
    return transformed_sequence
