def transform(sequence):
    arr = list(sequence)
    for i in range(len(arr)):
        if arr[i] == '2' or arr[i] == '6':
            break
        else:
            arr[i] = '0'
    for j in range(len(arr) - 1, -1, -1):
        if arr[j] == '2' or arr[j] == '6':
            break
        else:
            arr[j] = '0'
    return "".join(arr)

def transform(sequence):
    first_zero = sequence.find("00", 4)
    if first_zero != -1:
        sequence_1 = sequence[:first_zero].replace("2", "0").replace("6", "0")
        sequence_2 = sequence[first_zero:]
        return sequence_1 + sequence_2
    else:
        return sequence.replace("2", "0").replace("6", "0")
