def transform(sequence):
    first_num = sequence[0]
    zeros = sequence.count('0')
    num_count = len(sequence) - zeros
    new_sequence = '0' * zeros + first_num * num_count
    return new_sequence.replace(first_num + '0', first_num * 2, 1)

def transform(sequence):
    first_num = sequence[1]
    count_of_first_num = sequence.count(first_num)
    zeros = sequence.count("0")
    return "0"*zeros + first_num*count_of_first_num + "5" + "0"*(len(sequence) - count_of_first_num - zeros - 1)

def transform(sequence):
    index = sequence.index('0')
    return '0'*index + sequence[:index] + sequence[index+1:]
