def transform(sequence):
    seq = list(sequence)
    n = len(seq)
    for i in range(n):
        if seq[i] == '0':
            seq[i], seq[n-1] = seq[n-1], seq[i]
            n -= 1
    return ''.join(seq)

def transform(sequence):
    num_list = list(sequence)
    pivot_index = num_list.index(next(x for x in num_list if x != '0'))
    return ''.join(num_list[pivot_index:]) + ''.join(num_list[0:pivot_index])

def transform(sequence):
    non_zero_index = next((i for i, x in enumerate(sequence) if x != '0'), None)
    return sequence[non_zero_index:] + sequence[:non_zero_index]

def transform(sequence):
    sequence = list(sequence)
    zero_count = sequence.count('0')
    sequence.remove('0'*zero_count)
    sequence.insert(0, '0'*zero_count)
    return ''.join(sequence)

def transform(sequence):
    sequence = list(sequence)
    left = []
    right = []
    for i in sequence:
        if i == '0':
            left.append(i)
        else:
            right.append(i)
    transformed_sequence = ''.join(left+right)
    return transformed_sequence

