def transform(sequence):
    non_zero_index = sequence.find('0', 1)
    return sequence[:non_zero_index - 1] + '0' + sequence[non_zero_index - 1:] if non_zero_index != -1 else '0' + sequence[:-1]

def transform(sequence):
