def transform(sequence):
    first_half, second_half = sequence[:10], sequence[10:]
    return first_half.replace(first_half[0], '0') + second_half.replace(second_half[-1], '0') + second_half[-1]

def transform(sequence):
    index = sequence.find('0', 1)
    sequence = list(sequence)
    sequence[index-1], sequence[index] = sequence[index], sequence[index-1]
    return ''.join(sequence)

def transform(sequence):
    first_digit = None
    last_digit = None
    for i in range(len(sequence)):
        if sequence[i]!='0':
            first_digit = i
            break
    for i in range(len(sequence)-1, -1, -1):
        if sequence[i]!='0':
            last_digit = i
            break
    transformed_sequence = sequence[:first_digit] + '0' + sequence[first_digit:last_digit] + '0' + sequence[last_digit+1:]
    return transformed_sequence
