def transform(sequence):
    transformed = ''
    for i in range(len(sequence)):
        if sequence[i] == '0':
            transformed += '0'
        else:
            if i + 4 < len(sequence) and sequence[i + 4] == '7':
                transformed += '7'
            elif i + 4 < len(sequence) and sequence[i + 4] == '9':
                transformed += '9'
            else:
                transformed += sequence[i]
    return transformed
