def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)):
        if sequence[i] == '0':
            sequence[i] = '0'
        elif sequence[i] == '4':
            sequence[i] = '7'
        elif sequence[i] == '3':
            sequence[i] = '6'
        elif sequence[i] == '2':
            sequence[i] = '5'
        elif sequence[i] == '1':
            sequence[i] = '4'
    return ''.join(sequence)

def transform(sequence):
    trans = {'4': '7', '0': '0'}
    sequence = sequence.replace('4', '77').replace('40', '55').replace('44', '99')
    return ''.join([trans.get(i) if trans.get(i) else i for i in sequence])
