def transform(sequence):
    sequence_list = [int(i) for i in str(sequence)]
    for i in range(len(sequence_list)):
        if sequence_list[i] == 4:
            sequence_list[i] = 8
        elif sequence_list[i] == 0:
            sequence_list[i] = 0
        else:
            sequence_list[i] = 2 * sequence_list[i]
