def transform(sequence):
    sequence = list(sequence)
    n = len(sequence)
    for i in range(n-1, -1, -1):
        if sequence[i] != '0':
            break
    sequence = ['0']* (i+1) + sequence[:i+1]
    return ''.join(sequence[:n])
