def transform(sequence):
  non_zero_index = sequence.index(next(filter(lambda x: x!='0', sequence)))
  return '0' * non_zero_index + sequence[non_zero_index:] + '0'

def transform(sequence):
    number = sequence.strip('0')
    return '0' * (len(sequence) - len(number)) + number + '0'

def transform(sequence):
    count_zeros = sequence.count('0')
    max_index_non_zero = max([i for i in range(len(sequence)) if sequence[i]!='0'])
    return '0'*count_zeros + sequence[:max_index_non_zero+1] + '0'*(len(sequence) - max_index_non_zero - count_zeros - 1)

def transform(sequence):
    idx = sequence.index("4") if "4" in sequence else sequence.index("6")
    return sequence[idx:] + sequence[:idx]
