def transform(sequence):
    seq_list = list(sequence)
    index_0 = next((i for i, x in enumerate(seq_list) if x != '0'), None)
    index_encountered_0 = False
    for i in range(index_0, len(seq_list)):
        if seq_list[i] == '0':
            index_encountered_0 = i
            break
    if index_encountered_0:
        seq_list[index_0 - 1], seq_list[index_encountered_0] = seq_list[index_encountered_0], seq_list[index_0 - 1]
    return ''.join(seq_list)

