def transform(sequence):
    sequence = list(sequence)
    n = len(sequence)

    for i in range(n-1, -1, -1):
        if sequence[i] != '0':
            break

    for j in range(i-1, -1, -1):
        if sequence[j] == '0':
            sequence[i], sequence[j] = sequence[j], sequence[i]
            break

    return "".join(sequence)

